INSERT INTO `scriptmanage_script` VALUES (1, '测试脚本', '#!-*- coding: utf-8 -*-\nimport json\nimport paramiko\nimport os\n###$1$:HOST_IP:Linux机器IP地址\n###$2$:USERNAME:机器用户\n###$3$:PASSWORD:机器密码\n###$4$:windows_path:选择文件路径\n###$5$:linux_path:传到linux机器的路径\n\n\ndef list_file(file_path):\n\n    return [file for file in os.listdir(file_path) if os.path.isfile(os.path.join(file_path, file))]\n\nHOST_IP = $1$\nHOST_PORT = 22\nUSERNAME = $2$\nPASSWORD = $3$\nwindows_path = $4$\nlinux_path = $5$\n\na = windows_path.split(\'/\')\nif \'.\' in a[-1]:\n    linux_path = linux_path + \'/\' + a[-1]\n\nelse:\n    filenames = list_file(windows_path)\n    windows_path = windows_path + \'/\' + filenames[0]\n    b = windows_path.split(\'/\')\n    linux_path = linux_path + \'/\' + b[-1]\ntry:\n    transport = paramiko.Transport((HOST_IP, HOST_PORT))\n    transport.connect(username=USERNAME, password=PASSWORD)\n    sftp = paramiko.SFTPClient.from_transport(transport)\n    print(\"连接远程服务器成功！\")\n    print(\"*******************************同步文件--开始\")\n    sftp.put(windows_path, linux_path)\n    print(\"已同步本地文件： \\\"\" + windows_path + \"\\\"  到服务器路径：\\\"\" + linux_path + \"\\\"\")\nfinally:\n    sftp.close()\n    transport.close()\n\nprint(\"传输完成！\")\nprint(\"END\")', 'utf-8', 'was', 'python', '[[\"HOST_IP\", \"Linux机器IP地址\"], [\"USERNAME\", \"机器用户\"], [\"PASSWORD\", \"机器密码\"], [\"windows_path\", \"选择文件路径\"], [\"linux_path\", \"传到linux机器的路径\"]]', 'leon', 1521451803, 'w_l.py', NULL, NULL, NULL);

